    if basename.startswith('00'):
        return (0, 'preface', basename)

    # 付録
    if re.search(r'(appendix|付録)', basename, re.IGNORECASE):
        appendix_match = re.search(r'(appendix|付録)\s*([A-Z]|[ＡＢＣ])', basename, re.IGNORECASE)
