#!/usr/bin/env python3
import os
import re
from typing import List, Tuple

def extract_chapter_info(filename: str) -> Tuple[int, str, str]:
    """
    ファイル名から章番号、カテゴリ、原ファイル名を抽出する
    戻り値: (priority, category, original_filename)
    """
    basename = os.path.basename(filename)

    # 優先度とカテゴリを判定
    if re.search(r'(preface|前書き|はじめに|まえがき|序文|イントロダクション|目次|contents)', basename, re.IGNORECASE):
        return (0, 'preface', basename)

    if re.search(r'(about|著者|訳者|acknowledgments|謝辞)', basename, re.IGNORECASE):
        return (1, 'about', basename)

    # パート/部の判定（付録は除外）
    if not re.search(r'(appendix|付録)', basename, re.IGNORECASE):
        part_match = re.search(r'(part|第\s*[IVX]+\s*部|第\s*[０-９0-9]+\s*部)', basename, re.IGNORECASE)
        if part_match:
            part_num = extract_number_from_part(part_match.group(1))
            return (100 + part_num, 'part', basename)

    # ステージの判定
    stage_match = re.search(r'第\s*([０-９0-9]+)\s*ステージ', basename)
    if stage_match:
        stage_num = convert_japanese_number(stage_match.group(1))
        return (200 + stage_num, 'stage', basename)

    # 章番号の抽出（付録は除外）
    if not re.search(r'(appendix|付録)', basename, re.IGNORECASE):
        chapter_patterns = [
            r'chapter\s*([0-9]+)',
            r'([0-9]+)\s*章',
            r'([0-9]+)\.',
            r'^([0-9]+)\s',
            r'ステップ\s*([0-9]+)',
        ]

        for pattern in chapter_patterns:
            match = re.search(pattern, basename, re.IGNORECASE)
            if match:
                chapter_num = int(match.group(1))
                return (1000 + chapter_num, 'chapter', basename)

    # 00で始まるファイル（序文など）
    if basename.startswith('00'):
        return (0, 'preface', basename)

    # 付録
    if re.search(r'(appendix|付録)', basename, re.IGNORECASE):
        appendix_match = re.search(r'(appendix|付録)\s*([A-Z])', basename, re.IGNORECASE)
        if appendix_match:
            letter = appendix_match.group(2).upper()
            return (9000 + ord(letter) - ord('A'), 'appendix', basename)
        return (9000, 'appendix', basename)

    # 索引
    if re.search(r'(index|索引)', basename, re.IGNORECASE):
        return (9999, 'index', basename)

    # 参考文献
    if re.search(r'(references|参考文献|further reading)', basename, re.IGNORECASE):
        return (9998, 'references', basename)

    # 終わり
    if re.search(r'(おわりに|結び|afterword)', basename, re.IGNORECASE):
        return (9997, 'afterword', basename)

    # その他のファイル
    return (5000, 'other', basename)

def extract_number_from_part(part_str: str) -> int:
    """部番号を抽出"""
    # ローマ数字の処理
    roman_nums = {'I': 1, 'II': 2, 'III': 3, 'IV': 4, 'V': 5}
    for roman, num in roman_nums.items():
        if roman in part_str:
            return num

    # 数字の処理
    num_match = re.search(r'([０-９0-9]+)', part_str)
    if num_match:
        return convert_japanese_number(num_match.group(1))

    return 0

def convert_japanese_number(num_str: str) -> int:
    """全角数字を半角数字に変換"""
    japanese_numbers = {
        '０': '0', '１': '1', '２': '2', '３': '3', '４': '4',
        '５': '5', '６': '6', '７': '7', '８': '8', '９': '9'
    }

    result = num_str
    for jp_num, en_num in japanese_numbers.items():
        result = result.replace(jp_num, en_num)

    return int(result)

def sort_book_files(file_paths: List[str]) -> List[str]:
    """書籍ファイルを適切な順序でソート"""
    files_with_priority = []

    for file_path in file_paths:
        priority, category, basename = extract_chapter_info(file_path)
        files_with_priority.append((priority, category, basename, file_path))

    # 優先度でソート
    files_with_priority.sort(key=lambda x: (x[0], x[2]))

    return [item[3] for item in files_with_priority]

def main():
    import sys
    if len(sys.argv) != 2:
        print("Usage: python book_sorter.py <directory>")
        sys.exit(1)

    directory = sys.argv[1]
    if not os.path.exists(directory):
        print(f"Directory {directory} does not exist")
        sys.exit(1)

    # マークダウンファイルを取得
    md_files = []
    for filename in os.listdir(directory):
        if filename.endswith('.md'):
            md_files.append(os.path.join(directory, filename))

    # ソート
    sorted_files = sort_book_files(md_files)

    # 結果を表示
    for i, file_path in enumerate(sorted_files, 1):
        print(f"{i:2d}. {os.path.basename(file_path)}")

if __name__ == "__main__":
    main()
